import React, { useState, useEffect } from 'react';
import PokemonList from './PokemonList';
import axios from 'axios';
import Pagination from './Pagination';

function App() {
  const [pokemon, setPokemon] = useState([]);
  const [currentPageUrl, setCurrentPageUrl] = useState('https://pokeapi.co/api/v2/pokemon');
  const [nextPageUrl, setNextPageUrl] = useState();
  const [prevPageUrl, setPrevPageUrl] = useState();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    let cancel;
    axios.get(currentPageUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    }).then(res => {
    setLoading(false);
    setNextPageUrl(res.data.next);
    setPrevPageUrl(res.data.previous);
    setPokemon(res.data.results.map(p => p.name));
  });
  return () => {
    cancel();
  }

  }, [currentPageUrl]);

  //by using the useEffect hook, we tell React to re-run the code inside the function only when the second parameter changes. in our case the currentPageUrl value. You can use an Array for hose values if you wish to rerun based on more than one value.  
  function gotoNextPage() {
    setCurrentPageUrl(nextPageUrl);
  }
  function gotoPrevPage() {
    setCurrentPageUrl(prevPageUrl);
  }

  if (loading) return 'Loading...'

  return (
    <React.Fragment>
      <PokemonList pokemon={pokemon} />
    <Pagination
      gotoNextPage={gotoNextPage}
      gotoPrevPage={gotoPrevPage}
    />
    </React.Fragment>
  );
}

export default App;
